export default function Main() {

    function handleEditAvatarClick() {
    const popup = document.querySelector('.popup_type_avatar');
    popup.classList.add('popup_opened')        
    };

    function handleEditProfileClick() {
        const popup = document.querySelector('.popup_type_profile');
        popup.classList.add('popup_opened')
    };

    function handleAddPlaceClick() {
        const popup = document.querySelector('.popup_type_mesto');
        popup.classList.add('popup_opened')
    };

    return(
        <main className="main">
            <section className="profile">
                <div className="profile__images">
                    <img alt="Фото профиля" className="profile__avatar" />
                </div>
                <div className="profile__info">
                    <div className="profile__top-row">
                        <h1 className="profile__name"></h1>
                        <button className="profile__edit-button" type="button"></button>
                    </div>
                    <p className="profile__job"></p>
                </div>
                <button className="profile__add-button" type="button"></button>
            </section>
            <section className="mesto">
                <ul className="mesto__list">
                </ul>
             </section>
            </main>
    );
};
