import { useState, useEffect } from "react";
import api from "./utils/Api";
import Card from "./Card";

export default function Main({onEditProfile, onAddPlace, onEditAvatar}) {
    const [userName, setuserName] = useState("");
    const [userDescription, setuserDescription] = useState("");
    const [userAvatar, setuserAvatar] = useState("");
    const [initialCards, setinitialCard] = useState([]);

    useEffect(() => {
        api.getPageData()
        .then(([userData, cardsData]) => {
            setuserName(userData.name);
            setuserDescription(userData.about);
            setuserAvatar(userData.avatar);
            setinitialCard(...initialCards, cardsData)

        })
    },  [])


    return(
        <main className="main">
            <section className="profile">
                <div className="profile__images" onClick={onEditAvatar}>
                    <img alt="Фото профиля" className="profile__avatar" src={userAvatar} />
                </div>
                <div className="profile__info">
                    <div className="profile__top-row">
                        <h1 className="profile__name">{userName}</h1>
                        <button className="profile__edit-button" type="button" onClick={onEditProfile}></button>
                    </div>
                    <p className="profile__job">{userDescription}</p>
                </div>
                <button className="profile__add-button" type="button" onClick={onAddPlace}></button>
            </section>
            <section className="mesto">
                <ul  className="mesto__list">

                    {initialCards.map((card) => {
                        return(
                            <li key ={card.id} className="mesto__item">
                            <button className="mesto__delete-button"></button>
                            <img className="mesto__image" src={card.link} />
                            <div className="mesto__content">
                              <h2 className="mesto__title"></h2>
                              <div className="mesto__like-container">
                                <button className="mesto__like-button" type="button"></button>
                                <p className="mesto__like-counter"></p>
                              </div>
                             </div>
                          </li>
                        )

                    })}
                </ul>
             </section>
            </main>
    );
};
