import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";
import React, {useState, useEffect}  from 'react';
import EditProfilePopup from "./components/EditProfilePopup";
import AddPlacePopup from "./components/AddPlacePopup";
import EditAvatarPopup from "./components/EditAvatarPopup";
import ImagePopup from "./components/ImagePopup";
import api from "./components/utils/Api";
import { CurrentUserContext } from "./context/CurrentUserContext";
import { InitialCardsContext } from "./context/InitialCardsContext";

function App() {

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isimagePopup, setIsImagePopup] = useState(false);

  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);

  useEffect(() =>{
    api.getPageData()
    .then(([userData, cardsData]) => {
        setCurrentUser(userData);
        setInitialCards(cardsData);
    })
    .catch((err) => {
      console.log(`Ошибка.....: ${err}`);
    });
  },[]);

  function closeAllPopup(){
    setIsEditAvatarPopupOpen(false)
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false);
    setIsImagePopup(false);
    setSelectedCard({});
  };

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  };

  function handleEditProfileClick() {
     setIsEditProfilePopupOpen(true);
  };

  function handleAddPlaceClick() {
      setIsAddPlacePopupOpen(true);
  };

  function handleCardClick(card) {
    setIsImagePopup(true);
    setSelectedCard(card);
  };

  function handleCardLike(card) {

    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.likeCard(card._id, !isLiked).
    then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
} 

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <Header />
        <InitialCardsContext.Provider value={initialCards}>
          <Main 
            onEditProfile={handleEditProfileClick} 
            onAddPlace={handleAddPlaceClick} 
            onEditAvatar={handleEditAvatarClick} 
            onCardClick={handleCardClick} 
          />
        </InitialCardsContext.Provider>
        <Footer />
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose = {closeAllPopup} />
        <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose = {closeAllPopup} />
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose = {closeAllPopup} />
        <ImagePopup   card={selectedCard}  onClose={closeAllPopup} isOpen={isimagePopup} />
      </div>  
    </CurrentUserContext.Provider>
  );
};

export default App;
