import Header from "./components/Header";
import Main from "./components/Main";
import Footer from "./components/Footer";
import React, {useState}  from 'react';
import EditProfilePopup from "./components/EditProfilePopup";
import AddPlacePopup from "./components/AddPlacePopup";
import EditAvatarPopup from "./components/EditAvatarPopup";

function App() {

  const [isEditProfilePopupOpen, setisEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = useState(false);

  function handleEditAvatarClick() {
    setisEditAvatarPopupOpen(true)
  };

  function handleEditProfileClick() {
     setisEditProfilePopupOpen(true)
  };

  function handleAddPlaceClick() {
      setisAddPlacePopupOpen(true);
  };

  return (
    <div className="App">
      <Header />
      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} />
      <Footer />
      <EditProfilePopup isOpen={isEditProfilePopupOpen} />
      <AddPlacePopup isOpen={isAddPlacePopupOpen} />
      <EditAvatarPopup isOpen={isEditAvatarPopupOpen} />
    <div className="popup popup_type_delete">
      <div className="popup__container popup__container_small">
        <button className="popup__button-close" type="button"></button>
        <h2 className="popup__title">Вы уверены?</h2>
        <form 
        action="submit" 
        className="popup__form popup__form_type_delete" 
        name="delete"
        novalidate>
          <button className="popup__button" type="submit">Да</button>
        </form>
      </div>
    </div>
    <template className="mesto-template">
        <li className="mesto__item">
          <button className="mesto__delete-button"></button>
          <img
            className="mesto__image"
          />
          <div className="mesto__content">
            <h2 className="mesto__title"></h2>
            <div className="mesto__like-container">
              <button className="mesto__like-button" type="button"></button>
              <p className="mesto__like-counter"></p>
            </div>
          </div>
        </li>
    </template>
      <div className="popup popup_high-opacity popup_type_figure">
        <figure className="popup__figure">
          <button className="popup__button-close popup__button-close_figure popup__button-close_type_figure"></button>
          <img  className="popup__figure-image" />
          <figcaption className="popup__figure-subtitle"></figcaption>
        </figure>
      </div>
    </div>
  );
}

export default App;
