import { useEffect, useContext} from "react";
import { AppContext } from "../context/AppContext";
import regOk from "../images/regok.svg";
import regNot from "../images/regnot.svg"

const InfoTooltip = () => {


  const appContext = useContext(AppContext)
  const popupClose = appContext.closeAllPopups;

    return(
        <div onClick={e => (e.currentTarget === e.target) && popupClose()} className= {"popup popup_opened"}>
            <div className="popup__container">
                <button className="popup__button-close" type="button" onClick={popupClose} />
                <img className="popup__image" src={regOk} alt="Вы успешно зарегистрировались!" />
                <p className="popup__subtitle">Вы успешно зарегистрировались!</p>
            </div>    
        </div>
    )
}

export default InfoTooltip
