import { useEffect } from "react";

function ImagePopup({elem, onClose , isOpen}) {
   const card = elem;
   const popupClose = onClose;


    return(
        <div onClick={e => (e.currentTarget === e.target) && popupClose()} className={`popup popup_high-opacity popup_type_figure ${card ? 'popup_opened' : ''}`}>
        <figure className="popup__figure">
          <button className="popup__button-close popup__button-close_figure popup__button-close_type_figure"></button>
          <img  className="popup__figure-image" />
          <figcaption className="popup__figure-subtitle"></figcaption>
        </figure>
      </div>

    )}

    export default ImagePopup
