import { useEffect } from "react"
import { useRef } from "react";

function PopupWithForm({name, title, children, button, isOpen, onClose}){

    const handleCloseWithEsc = onClose;
    const ref = useRef();




    useEffect(() => {
        document.addEventListener('keydown', handleCloseWithEsc);
        return() => {
            document.removeEventListener('keydown', handleCloseWithEsc)
        }},
        [])

    useEffect(() => {
        document.addEventListener("click", (event) => {
            if(event.target = ref.current){
                console.log('as');
            }
        })
    })

    return(
        <div className= {`popup popup_type_${name} ${isOpen ? 'popup_opened' : ''}`}>
            <div className="popup__container">
                <button className="popup__button-close" type="button" onClick={onClose}></button>
                <h2 className='popup__title'>{title}</h2>
                <form 
                    action="submit" 
                    className={`popup__form popup__form_type_${name}`} 
                    name={name}
                    noValidate>
                        {children}
                </form>
                <button className="popup__button" type="submit">{button}</button>
            </div>
        </div>
    )
}

export default PopupWithForm
