import { useEffect } from "react"
import { useRef, useState } from "react";

function PopupWithForm({name, title, children, button, isOpen, onClose}){
    const [isActive, setisActive] = useState(true);
    const popupClose = onClose;
    const ref = useRef();


    function popupCloseEsc(event) {
        if(event.key === 'Escape') {
            popupClose()
        }
    }
    function popupCloseClick(event){
        if(ref.current && !ref.current.contains(event.target)){
            setisActive(!isActive)

        }

    }


    useEffect(() => {
        document.addEventListener('keydown', popupCloseEsc);
        return() => {
            document.removeEventListener('keydown', popupCloseEsc)
        }},
        [])

    useEffect(() => {
        document.addEventListener("click", popupCloseClick);
        return() => {
            document.removeEventListener('click', popupCloseClick)
        }
    })


    return(
        <div ref={ref}  className= {`popup popup_type_${name} ${isOpen ? 'popup_opened' : ''}`}>
            <div  className="popup__container">
                <button className="popup__button-close" type="button" onClick={onClose}></button>
                <h2 className='popup__title'>{title}</h2>
                <form 
                    action="submit" 
                    className={`popup__form popup__form_type_${name}`} 
                    name={name}
                    noValidate>
                        {children}
                </form>
                <button className="popup__button" type="submit">{button}</button>
            </div>
        </div>
    )
}

export default PopupWithForm
