import { useEffect, useContext} from "react";
import { AppContext } from "../context/AppContext";


const InfoTooltip = () => {


  const appContext = useContext(AppContext)
  const popupClose = appContext.closeAllPopups;

    return(
        <div onClick={e => (e.currentTarget === e.target) && popupClose()} className= {"popup popup_opened"}>
            <figure className="popup__figure">
                <button className="popup__button-close popup__button-close_figure popup__button-close_type_figure" onClick={popupClose}></button>
                <img className="popup__figure-image" src='../images/regok.svg'/>
                <figcaption className="popup__figure-subtitle">Вы успешно зарегистрировались!</figcaption>
            </figure>    
        </div>
    )
}

export default InfoTooltip
